import React, { useState, useEffect } from 'react';
import { Database, Code2, Globe, Server, Zap, TrendingUp, Star, Github, ExternalLink } from 'lucide-react';

interface Repository {
  name: string;
  description: string;
  language: string;
  topics: string[];
  stargazers_count: number;
  forks_count: number;
  html_url: string;
  updated_at: string;
}

interface SkillData {
  name: string;
  level: number;
  experience: string;
  icon: string;
  repos: number;
  lastUsed: string;
}

const Skills: React.FC = () => {
  const [repositories, setRepositories] = useState<Repository[]>([]);
  const [skills, setSkills] = useState<SkillData[]>([]);
  const [loading, setLoading] = useState(true);
  const [githubStats, setGithubStats] = useState({
    repositories: 0,
    followers: 1,
    following: 2,
    stars: 0,
    location: "Delhi, India",
    focus: "Backend Development using Java"
  });

  useEffect(() => {
    fetchGitHubData();
  }, []);

  const fetchGitHubData = async () => {
    try {
      // Fetch repositories
      const reposResponse = await fetch('https://api.github.com/users/Nitish-11k/repos?sort=updated&per_page=100');
      const reposData: Repository[] = await reposResponse.json();

      if (reposData.length > 0) {
        setRepositories(reposData);

        // Calculate skills from repositories
        const skillsMap = new Map<string, SkillData>();

        reposData.forEach(repo => {
          if (repo.language) {
            const existing = skillsMap.get(repo.language);
            if (existing) {
              existing.repos += 1;
              existing.level = Math.min(100, existing.level + 5);
              if (new Date(repo.updated_at) > new Date(existing.lastUsed)) {
                existing.lastUsed = repo.updated_at;
              }
            } else {
              skillsMap.set(repo.language, {
                name: repo.language,
                level: 70,
                experience: "1+ years",
                icon: getLanguageIcon(repo.language),
                repos: 1,
                lastUsed: repo.updated_at
              });
            }
          }

          // Extract skills from topics
          repo.topics?.forEach(topic => {
            const skillName = mapTopicToSkill(topic);
            if (skillName) {
              const existing = skillsMap.get(skillName);
              if (existing) {
                existing.repos += 1;
                existing.level = Math.min(100, existing.level + 3);
              } else {
                skillsMap.set(skillName, {
                  name: skillName,
                  level: 65,
                  experience: "1+ years",
                  icon: getSkillIcon(skillName),
                  repos: 1,
                  lastUsed: repo.updated_at
                });
              }
            }
          });
        });

        // Convert to array and sort by level
        const skillsArray = Array.from(skillsMap.values())
          .sort((a, b) => b.level - a.level)
          .slice(0, 20); // Top 20 skills

        setSkills(skillsArray);

        // Update GitHub stats
        const totalStars = reposData.reduce((sum, repo) => sum + repo.stargazers_count, 0);
        setGithubStats(prev => ({
          ...prev,
          repositories: reposData.length,
          stars: totalStars
        }));
      }
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
      // Fallback to static data if API fails
      setSkills(getFallbackSkills());
    } finally {
      setLoading(false);
    }
  };

  const getLanguageIcon = (language: string): string => {
    const icons: { [key: string]: string } = {
      'Java': '‚òï',
      'Python': 'üêç',
      'JavaScript': '‚ö°',
      'TypeScript': 'üìò',
      'HTML': 'üåê',
      'CSS': 'üé®',
      'C++': '‚öôÔ∏è',
      'C': 'üîß',
      'PHP': 'üêò',
      'Go': 'üêπ',
      'Rust': 'ü¶Ä',
      'Kotlin': 'üì±',
      'Swift': 'üçé',
      'Ruby': 'üíé',
      'Scala': '‚ö°',
      'Shell': 'üêö',
      'Dockerfile': 'üê≥',
      'Makefile': 'üî®'
    };
    return icons[language] || 'üíª';
  };

  const getSkillIcon = (skill: string): string => {
    const icons: { [key: string]: string } = {
      'spring': 'üçÉ',
      'spring-boot': 'üçÉ',
      'jwt': 'üîê',
      'authentication': 'üîê',
      'api': 'üåê',
      'rest': 'üåê',
      'database': 'üóÑÔ∏è',
      'mysql': 'üê¨',
      'mongodb': 'üçÉ',
      'postgresql': 'üêò',
      'docker': 'üê≥',
      'kubernetes': '‚ò∏Ô∏è',
      'aws': '‚òÅÔ∏è',
      'azure': '‚òÅÔ∏è',
      'gcp': '‚òÅÔ∏è',
      'react': '‚öõÔ∏è',
      'angular': 'üÖ∞Ô∏è',
      'vue': 'üíö',
      'nodejs': 'üü¢',
      'express': 'üöÇ',
      'git': 'üìö',
      'github': 'üìö',
      'testing': 'üß™',
      'junit': 'üß™',
      'mockito': 'üß™',
      'maven': 'üì¶',
      'gradle': 'üì¶',
      'jenkins': 'ü§ñ',
      'ci-cd': 'üîÑ',
      'microservices': 'üîß',
      'architecture': 'üèóÔ∏è'
    };
    return icons[skill.toLowerCase()] || '‚ö°';
  };

  const mapTopicToSkill = (topic: string): string | null => {
    const skillMappings: { [key: string]: string } = {
      'spring': 'Spring Framework',
      'spring-boot': 'Spring Boot',
      'jwt': 'JWT Authentication',
      'authentication': 'Authentication',
      'api': 'REST APIs',
      'rest': 'REST APIs',
      'database': 'Database Design',
      'mysql': 'MySQL',
      'mongodb': 'MongoDB',
      'postgresql': 'PostgreSQL',
      'docker': 'Docker',
      'kubernetes': 'Kubernetes',
      'aws': 'AWS',
      'azure': 'Azure',
      'gcp': 'Google Cloud',
      'react': 'React.js',
      'angular': 'Angular',
      'vue': 'Vue.js',
      'nodejs': 'Node.js',
      'express': 'Express.js',
      'git': 'Git',
      'github': 'GitHub',
      'testing': 'Testing',
      'junit': 'JUnit',
      'mockito': 'Mockito',
      'maven': 'Maven',
      'gradle': 'Gradle',
      'jenkins': 'Jenkins',
      'ci-cd': 'CI/CD',
      'microservices': 'Microservices',
      'architecture': 'System Architecture'
    };
    return skillMappings[topic.toLowerCase()] || null;
  };

  const getFallbackSkills = (): SkillData[] => [
    { name: "Java", level: 95, experience: "3+ years", icon: "‚òï", repos: 8, lastUsed: new Date().toISOString() },
    { name: "Spring Framework", level: 90, experience: "2+ years", icon: "üçÉ", repos: 6, lastUsed: new Date().toISOString() },
    { name: "JWT Authentication", level: 85, experience: "2+ years", icon: "üîê", repos: 4, lastUsed: new Date().toISOString() },
    { name: "MySQL", level: 85, experience: "2+ years", icon: "üê¨", repos: 5, lastUsed: new Date().toISOString() },
    { name: "MongoDB", level: 80, experience: "1+ years", icon: "üçÉ", repos: 3, lastUsed: new Date().toISOString() },
    { name: "HTML5", level: 90, experience: "3+ years", icon: "üåê", repos: 10, lastUsed: new Date().toISOString() },
    { name: "CSS3", level: 85, experience: "3+ years", icon: "üé®", repos: 10, lastUsed: new Date().toISOString() },
    { name: "JavaScript", level: 75, experience: "2+ years", icon: "‚ö°", repos: 6, lastUsed: new Date().toISOString() },
    { name: "Git", level: 90, experience: "3+ years", icon: "üìö", repos: 20, lastUsed: new Date().toISOString() },
    { name: "REST APIs", level: 88, experience: "2+ years", icon: "üåê", repos: 7, lastUsed: new Date().toISOString() }
  ];

  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - date.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return 'Today';
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) return `${Math.floor(diffDays / 7)} weeks ago`;
    if (diffDays < 365) return `${Math.floor(diffDays / 30)} months ago`;
    return `${Math.floor(diffDays / 365)} years ago`;
  };

  const getSkillLevelColor = (level: number): string => {
    if (level >= 90) return "from-green-500 to-green-600";
    if (level >= 80) return "from-blue-500 to-blue-600";
    if (level >= 70) return "from-yellow-500 to-yellow-600";
    return "from-orange-500 to-orange-600";
  };

  return (
    <section id="skills" className="section-padding bg-white">
      <div className="container-max">
        <div className="text-center mb-16">
          <h2 className="text-3xl sm:text-4xl font-bold text-secondary-900 mb-4">
            Skills & Expertise
          </h2>
          <p className="text-lg text-secondary-600 max-w-2xl mx-auto">
            Dynamically fetched from my GitHub repositories and real-world projects
          </p>
        </div>

        {/* GitHub Stats Overview */}
        <div className="bg-gradient-to-r from-primary-50 to-purple-50 rounded-2xl p-8 mb-16">
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 rounded-full mb-4">
              <TrendingUp className="w-8 h-8 text-primary-600" />
            </div>
            <h3 className="text-2xl font-semibold text-secondary-900 mb-2">
              GitHub Profile Overview
            </h3>
            <p className="text-secondary-600">
              Real metrics from my development journey
            </p>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600 mb-2">{githubStats.repositories}</div>
              <div className="text-sm text-secondary-600">Repositories</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600 mb-2">{githubStats.followers}</div>
              <div className="text-sm text-secondary-600">Followers</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600 mb-2">{githubStats.stars}</div>
              <div className="text-sm text-secondary-600">Stars</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600 mb-2">üáÆüá≥</div>
              <div className="text-sm text-secondary-600">Delhi, India</div>
            </div>
          </div>

          <div className="text-center mt-6">
            <p className="text-primary-700 font-medium">
              üéØ {githubStats.focus}
            </p>
          </div>
        </div>

        {/* Skills Grid */}
        {loading ? (
          <div className="text-center py-12">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 rounded-full mb-4">
              <Github className="w-8 h-8 text-primary-600 animate-spin" />
            </div>
            <p className="text-secondary-600">Fetching skills from GitHub...</p>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 mb-16">
            {skills.map((skill, index) => (
              <div key={index} className="bg-white rounded-xl shadow-lg p-6 border border-secondary-100 hover:shadow-xl transition-all duration-300">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <span className="text-2xl">{skill.icon}</span>
                    <div>
                      <h4 className="font-semibold text-secondary-900">{skill.name}</h4>
                      <p className="text-sm text-secondary-500">{skill.experience}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-lg font-bold text-primary-600">{skill.level}%</div>
                    <div className="text-xs text-secondary-500">{skill.repos} repos</div>
                  </div>
                </div>

                <div className="w-full bg-secondary-200 rounded-full h-3 mb-3">
                  <div
                    className={`bg-gradient-to-r ${getSkillLevelColor(skill.level)} h-3 rounded-full transition-all duration-1000 ease-out relative overflow-hidden`}
                    style={{ width: `${skill.level}%` }}
                  >
                    <div className="absolute inset-0 bg-white opacity-20 animate-pulse"></div>
                  </div>
                </div>

                <div className="text-xs text-secondary-500">
                  Last used: {formatDate(skill.lastUsed)}
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Top Repositories */}
        {repositories.length > 0 && (
          <div className="bg-gradient-to-r from-primary-50 to-purple-50 rounded-2xl p-8 mb-16">
            <div className="text-center mb-8">
              <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 rounded-full mb-4">
                <Star className="w-8 h-8 text-primary-600" />
              </div>
              <h3 className="text-2xl font-semibold text-secondary-900 mb-2">
                Top Repositories
              </h3>
              <p className="text-secondary-600">
                My most active and popular projects
              </p>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              {repositories.slice(0, 6).map((repo, index) => (
                <div
                  key={index}
                  className="bg-white/70 backdrop-blur-sm rounded-lg p-6 border border-white/50 hover:bg-white hover:shadow-md transition-all duration-300"
                >
                  <div className="flex items-start justify-between mb-3">
                    <h4 className="font-semibold text-secondary-900 text-lg">
                      {repo.name}
                    </h4>
                    <a
                      href={repo.html_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-primary-600 hover:text-primary-700 transition-colors duration-200"
                    >
                      <ExternalLink className="w-4 h-4" />
                    </a>
                  </div>

                  {repo.description && (
                    <p className="text-secondary-600 text-sm mb-3 line-clamp-2">
                      {repo.description}
                    </p>
                  )}

                  <div className="flex items-center justify-between text-sm text-secondary-500">
                    <div className="flex items-center space-x-4">
                      {repo.language && (
                        <span className="flex items-center space-x-1">
                          <span className="w-3 h-3 bg-primary-500 rounded-full"></span>
                          <span>{repo.language}</span>
                        </span>
                      )}
                      <span>‚≠ê {repo.stargazers_count}</span>
                      <span>üç¥ {repo.forks_count}</span>
                    </div>
                    <span>{formatDate(repo.updated_at)}</span>
                  </div>
                </div>
              ))}
            </div>

            <div className="text-center mt-6">
              <a
                href="https://github.com/Nitish-11k?tab=repositories"
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center px-6 py-3 bg-primary-600 text-white font-semibold rounded-lg hover:bg-primary-700 transition-all duration-300"
              >
                <Github className="w-5 h-5 mr-2" />
                View All Repositories
              </a>
            </div>
          </div>
        )}

        {/* Learning Journey */}
        <div className="text-center mt-16">
          <h3 className="text-2xl font-semibold text-secondary-900 mb-4">
            Always Learning & Growing
          </h3>
          <p className="text-lg text-secondary-600 max-w-3xl mx-auto mb-6">
            Currently diving into <strong>Frontend Development</strong> with React and TypeScript, while continuing to 
            expand my knowledge in cloud architecture, microservices, and advanced Java patterns. Working towards 
            becoming a full-stack developer!
          </p>
          <div className="inline-flex items-center px-6 py-3 bg-primary-100 text-primary-700 rounded-lg">
            <Star className="w-5 h-5 mr-2" />
            <span className="font-medium">Focusing on Backend Development using Java</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
