import React, { useEffect, useState } from 'react';
import { ArrowDown, Github, Linkedin, Mail } from 'lucide-react';

const Hero: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const scrollToAbout = () => {
    const element = document.querySelector('#about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const techStack = [
    { name: 'Java', icon: '‚òï', color: 'from-orange-500 to-red-500', delay: '0s' },
    { name: 'Spring Boot', icon: 'üçÉ', color: 'from-green-500 to-emerald-500', delay: '0.2s' },
    { name: 'TypeScript', icon: 'üî∑', color: 'from-blue-500 to-indigo-500', delay: '0.4s' },
    { name: 'React', icon: '‚öõÔ∏è', color: 'from-cyan-500 to-blue-500', delay: '0.6s' },
    { name: 'MongoDB', icon: 'üçÉ', color: 'from-green-600 to-emerald-600', delay: '0.8s' },
    { name: 'Firebase', icon: 'üî•', color: 'from-orange-500 to-yellow-500', delay: '1s' },
    { name: 'Docker', icon: 'üê≥', color: 'from-blue-600 to-cyan-600', delay: '1.2s' },
    { name: 'Git', icon: 'üìö', color: 'from-orange-600 to-red-600', delay: '1.4s' }
  ];

  return (
    <section 
      id="home" 
      className="min-h-screen flex items-center justify-center relative overflow-hidden dark-section"
      onMouseEnter={() => setIsHovering(true)}
      onMouseLeave={() => setIsHovering(false)}
    >
      {/* Background Pattern */}
      <div className="absolute inset-0 bg-gradient-to-br from-dark-900 via-dark-800 to-dark-700"></div>
      <div className="absolute inset-0 opacity-20">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, #fca311 1px, transparent 0)`,
          backgroundSize: '60px 60px'
        }}></div>
      </div>

      {/* Tech Stack Logos - Appear on Hover */}
      <div className="absolute inset-0 pointer-events-none">
        {techStack.map((tech, index) => (
          <div
            key={index}
            className={`absolute transition-all duration-700 ease-out transform ${
              isHovering 
                ? 'opacity-100 scale-100 translate-y-0' 
                : 'opacity-0 scale-75 translate-y-4'
            }`}
            style={{
              top: `${20 + (index * 8)}%`,
              left: `${15 + (index * 10)}%`,
              transitionDelay: tech.delay,
              animationDelay: tech.delay
            }}
          >
            <div className={`bg-gradient-to-r ${tech.color} text-white rounded-2xl px-4 py-3 shadow-2xl backdrop-blur-sm border border-white/20 transform hover:scale-110 transition-transform duration-300`}>
              <div className="text-2xl mb-1">{tech.icon}</div>
              <div className="text-xs font-semibold text-center">{tech.name}</div>
            </div>
          </div>
        ))}

        {/* Additional Tech Logos on Right Side */}
        {techStack.slice(0, 4).map((tech, index) => (
          <div
            key={`right-${index}`}
            className={`absolute transition-all duration-700 ease-out transform ${
              isHovering 
                ? 'opacity-100 scale-100 translate-y-0' 
                : 'opacity-0 scale-75 translate-y-4'
