import React, { useEffect, useState } from 'react';
import { ArrowDown, Github, Linkedin, Mail } from 'lucide-react';

const Hero: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const scrollToAbout = () => {
    const element = document.querySelector('#about');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Only update position if cursor is in safe zones
    const isLeftSide = x < window.innerWidth * 0.4;
    const isRightSide = x > window.innerWidth * 0.6;

    if (isLeftSide || isRightSide) {
      setMousePosition({ x, y });
      setIsHovering(true);
    } else {
      setIsHovering(false);
    }
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  const techStack = [
    { name: 'Java', icon: '‚òï', color: 'from-orange-500 to-red-500', delay: '0s' },
    { name: 'Spring Boot', icon: 'üçÉ', color: 'from-green-500 to-emerald-500', delay: '0.1s' },
    { name: 'TypeScript', icon: 'üî∑', color: 'from-blue-500 to-indigo-500', delay: '0.2s' },
    { name: 'React', icon: '‚öõÔ∏è', color: 'from-cyan-500 to-blue-500', delay: '0.3s' },
    { name: 'MongoDB', icon: 'üçÉ', color: 'from-green-600 to-emerald-600', delay: '0.4s' },
    { name: 'Firebase', icon: 'üî•', color: 'from-orange-500 to-yellow-500', delay: '0.5s' },
    { name: 'Docker', icon: 'üê≥', color: 'from-blue-600 to-cyan-600', delay: '0.6s' },
    { name: 'Git', icon: 'üìö', color: 'from-orange-600 to-red-600', delay: '0.7s' }
  ];

  return (
    <section 
      id="home" 
      className="min-h-screen flex items-center justify-center relative overflow-hidden dark-section"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      {/* Background Pattern */}
      <div className="absolute inset-0 bg-gradient-to-br from-dark-900 via-dark-800 to-dark-700"></div>
      <div className="absolute inset-0 opacity-20">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, #fca311 1px, transparent 0)`,
          backgroundSize: '60px 60px'
        }}></div>
      </div>

      {/* Tech Stack Logos - Only visible around cursor, avoiding content areas */}
      <div className="absolute inset-0 pointer-events-none">
        {isHovering && techStack.map((tech, index) => {
          // Calculate safe zones - avoid left column (text) and right column (image) areas
          const isLeftSide = mousePosition.x < window.innerWidth * 0.4; // Left 40% of screen
          const isRightSide = mousePosition.x > window.innerWidth * 0.6; // Right 40% of screen
          const isCenterArea = mousePosition.x >= window.innerWidth * 0.4 && mousePosition.x <= window.innerWidth * 0.6;

          // Skip rendering if cursor is in content areas
          if (isCenterArea) return null;

          // Calculate position relative to cursor with some offset
          const offsetX = (index % 3 - 1) * 120; // Spread horizontally
          const offsetY = Math.floor(index / 3) * 80 - 40; // Spread vertically

          // Ensure icons stay within safe boundaries
          let finalX = mousePosition.x + offsetX;
          let finalY = mousePosition.y + offsetY;

          // Keep icons within viewport bounds
          if (finalX < 20) finalX = 20;
          if (finalX > window.innerWidth - 140) finalX = window.innerWidth - 140;
          if (finalY < 20) finalY = 20;
          if (finalY > window.innerHeight - 100) finalY = window.innerHeight - 100;

          // Additional safety check - don't render if too close to content
          const leftColumnRight = window.innerWidth * 0.4;
          const rightColumnLeft = window.innerWidth * 0.6;

          if (finalX > leftColumnRight - 100 && finalX < rightColumnLeft + 100) return null;

          return (
            <div
              key={index}
              className="absolute transition-all duration-300 ease-out transform"
              style={{
                left: `${finalX}px`,
                top: `${finalY}px`,
                transitionDelay: tech.delay,
                zIndex: 1000
              }}
            >
