import React, { useState, useEffect } from 'react';
import { Database, Code2, Globe, Server, Zap, TrendingUp, Star, Github, ExternalLink } from 'lucide-react';

interface Repository {
  name: string;
  description: string;
  language: string;
  topics: string[];
  stargazers_count: number;
  forks_count: number;
  html_url: string;
  updated_at: string;
}

interface SkillData {
  name: string;
  level: number;
  experience: string;
  icon: string;
  repos: number;
  lastUsed: string;
}

const Skills: React.FC = () => {
  const [repositories, setRepositories] = useState<Repository[]>([]);
  const [skills, setSkills] = useState<SkillData[]>([]);
  const [loading, setLoading] = useState(true);
  const [githubStats, setGithubStats] = useState({
    repositories: 0,
    followers: 1,
    following: 2,
    stars: 0,
    location: "Delhi, India",
    focus: "Backend Development using Java"
  });

  useEffect(() => {
    fetchGitHubData();
  }, []);

  const fetchGitHubData = async () => {
    try {
      // Fetch repositories
      const reposResponse = await fetch('https://api.github.com/users/Nitish-11k/repos?sort=updated&per_page=100');
      const reposData: Repository[] = await reposResponse.json();

      if (reposData.length > 0) {
        setRepositories(reposData);

        // Calculate skills from repositories
        const skillsMap = new Map<string, SkillData>();

        reposData.forEach(repo => {
          if (repo.language) {
            const existing = skillsMap.get(repo.language);
            if (existing) {
              existing.repos += 1;
              existing.level = Math.min(100, existing.level + 5);
              if (new Date(repo.updated_at) > new Date(existing.lastUsed)) {
                existing.lastUsed = repo.updated_at;
              }
            } else {
              skillsMap.set(repo.language, {
                name: repo.language,
                level: 70,
                experience: "1+ years",
                icon: getLanguageIcon(repo.language),
                repos: 1,
                lastUsed: repo.updated_at
              });
            }
          }

          // Extract skills from topics
          repo.topics?.forEach(topic => {
            const skillName = mapTopicToSkill(topic);
            if (skillName) {
              const existing = skillsMap.get(skillName);
              if (existing) {
                existing.repos += 1;
                existing.level = Math.min(100, existing.level + 3);
              } else {
                skillsMap.set(skillName, {
                  name: skillName,
                  level: 65,
                  experience: "1+ years",
                  icon: getSkillIcon(skillName),
                  repos: 1,
                  lastUsed: repo.updated_at
                });
              }
            }
          });
        });

        // Convert to array and sort by level
        const skillsArray = Array.from(skillsMap.values())
          .sort((a, b) => b.level - a.level)
          .slice(0, 20); // Top 20 skills

        setSkills(skillsArray);

        // Update GitHub stats
        const totalStars = reposData.reduce((sum, repo) => sum + repo.stargazers_count, 0);
        setGithubStats(prev => ({
          ...prev,
          repositories: reposData.length,
          stars: totalStars
        }));
      }
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
      // Fallback to static data if API fails
      setSkills(getFallbackSkills());
    } finally {
      setLoading(false);
    }
  };

  const getLanguageIcon = (language: string): string => {
    const icons: { [key: string]: string } = {
      'Java': '☕',
      'Python': '🐍',
      'JavaScript': '⚡',
      'TypeScript': '📘',
      'HTML': '🌐',
      'CSS': '🎨',
      'C++': '⚙️',
      'C': '🔧',
      'PHP': '🐘',
      'Go': '🐹',
      'Rust': '🦀',
      'Kotlin': '📱',
      'Swift': '🍎',
      'Ruby': '💎',
      'Scala': '⚡',
      'Shell': '🐚',
      'Dockerfile': '🐳',
      'Makefile': '🔨'
    };
    return icons[language] || '💻';
  };

  const getSkillIcon = (skill: string): string => {
    const icons: { [key: string]: string } = {
      'spring': '🍃',
      'spring-boot': '🍃',
      'jwt': '🔐',
      'authentication': '🔐',
      'api': '🌐',
      'rest': '🌐',
      'database': '🗄️',
      'mysql': '🐬',
      'mongodb': '🍃',
      'postgresql': '🐘',
      'docker': '🐳',
      'kubernetes': '☸️',
      'aws': '☁️',
      'azure': '☁️',
      'gcp': '☁️',
      'react': '⚛️',
      'angular': '🅰️',
      'vue': '💚',
      'nodejs': '🟢',
      'express': '🚂',
      'git': '📚',
      'github': '📚',
      'testing': '🧪',
      'junit': '🧪',
      'mockito': '🧪',
      'maven': '📦',
      'gradle': '📦',
      'jenkins': '🤖',
      'ci-cd': '🔄',
      'microservices': '🔧',
      'architecture': '🏗️'
    };
    return icons[skill.toLowerCase()] || '⚡';
  };

  const mapTopicToSkill = (topic: string): string | null => {
    const skillMappings: { [key: string]: string } = {
      'spring': 'Spring Framework',
      'spring-boot': 'Spring Boot',
      'jwt': 'JWT Authentication',
      'authentication': 'Authentication',
      'api': 'REST APIs',
      'rest': 'REST APIs',
      'database': 'Database Design',
      'mysql': 'MySQL',
      'mongodb': 'MongoDB',
      'postgresql': 'PostgreSQL',
      'docker': 'Docker',
      'kubernetes': 'Kubernetes',
      'aws': 'AWS',
      'azure': 'Azure',
      'gcp': 'Google Cloud',
      'react': 'React.js',
      'angular': 'Angular',
      'vue': 'Vue.js',
      'nodejs': 'Node.js',
      'express': 'Express.js',
      'git': 'Git',
      'github': 'GitHub',
      'testing': 'Testing',
      'junit': 'JUnit',
      'mockito': 'Mockito',
      'maven': 'Maven',
      'gradle': 'Gradle',
      'jenkins': 'Jenkins',
      'ci-cd': 'CI/CD',
      'microservices': 'Microservices',
      'architecture': 'System Architecture'
    };
    return skillMappings[topic.toLowerCase()] || null;
  };

  const getFallbackSkills = (): SkillData[] => [
    { name: "Java", level: 95, experience: "3+ years", icon: "☕", repos: 8, lastUsed: new Date().toISOString() },
    { name: "Spring Framework", level: 90, experience: "2+ years", icon: "🍃", repos: 6, lastUsed: new Date().toISOString() },
    { name: "JWT Authentication", level: 85, experience: "2+ years", icon: "🔐", repos: 4, lastUsed: new Date().toISOString() },
    { name: "MySQL", level: 85, experience: "2+ years", icon: "🐬", repos: 5, lastUsed: new Date().toISOString() },
    { name: "MongoDB", level: 80, experience: "1+ years", icon: "🍃", repos: 3, lastUsed: new Date().toISOString() },
    { name: "HTML5", level: 90, experience: "3+ years", icon: "🌐", repos: 10, lastUsed: new Date().toISOString() },
    { name: "CSS3", level: 85, experience: "3+ years", icon: "🎨", repos: 10, lastUsed: new Date().toISOString() },
    { name: "JavaScript", level: 75, experience: "2+ years", icon: "⚡", repos: 6, lastUsed: new Date().toISOString() },
    { name: "Git", level: 90, experience: "3+ years", icon: "📚", repos: 20, lastUsed: new Date().toISOString() },
    { name: "REST APIs", level: 88, experience: "2+ years", icon: "🌐", repos: 7, lastUsed: new Date().toISOString() }
  ];

  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - date.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return 'Today';
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) return `${Math.floor(diffDays / 7)} weeks ago`;
    if (diffDays < 365) return `${Math.floor(diffDays / 30)} months ago`;
    return `${Math.floor(diffDays / 365)} years ago`;
  };

  const getSkillLevelColor = (level: number): string => {
    if (level >= 90) return "from-green-500 to-green-600";
    if (level >= 80) return "from-blue-500 to-blue-600";
    if (level >= 70) return "from-yellow-500 to-yellow-600";
    return "from-orange-500 to-orange-600";
  };

  const getSkillLevelGradient = (level: number): string => {
    if (level >= 90) return "from-emerald-400 to-teal-500";
    if (level >= 80) return "from-blue-400 to-cyan-500";
    if (level >= 70) return "from-yellow-400 to-orange-500";
    return "from-orange-400 to-red-500";
  };

  // 3D Bar Chart Component
  const SkillBarChart: React.FC<{ skill: SkillData }> = ({ skill }) => {
    const barHeight = (skill.level / 100) * 200; // Max height 200px
    const barWidth = 60;

    return (
      <div className="relative group">
        {/* 3D Bar Container */}
        <div className="relative transform rotate-x-12 perspective-1000">
          {/* Main Bar */}
          <div 
            className="relative bg-gradient-to-t from-accent-400 to-accent-500 rounded-t-lg shadow-lg transition-all duration-500 group-hover:shadow-2xl group-hover:scale-105"
            style={{ 
              width: `${barWidth}px`, 
              height: `${barHeight}px`,
              transform: 'rotateX(15deg)',
              transformStyle: 'preserve-3d'
            }}
          >
            {/* Bar Top Face */}
            <div 
              className="absolute inset-0 bg-gradient-to-r from-accent-300 to-accent-400 rounded-t-lg"
              style={{ transform: 'rotateX(-15deg) translateZ(10px)' }}
            />

            {/* Bar Right Face */}
            <div 
              className="absolute right-0 top-0 bg-gradient-to-r from-accent-500 to-accent-600 rounded-tr-lg"
              style={{ 
                width: '10px', 
                height: `${barHeight}px`,
                transform: 'rotateY(90deg) translateZ(5px)'
              }}
            />

            {/* Bar Left Face */}
            <div 
              className="absolute left-0 top-0 bg-gradient-to-r from-accent-600 to-accent-700 rounded-tl-lg"
              style={{ 
                width: '10px', 
                height: `${barHeight}px`,
                transform: 'rotateY(-90deg) translateZ(5px)'
              }}
            />

            {/* 3D Arrow */}
            <div 
              className="absolute -top-8 -left-4 w-16 h-16 text-accent-500 opacity-80"
              style={{ 
                transform: 'rotateX(-15deg) translateZ(20px)',
                filter: 'drop-shadow(2px 2px 4px rgba(0,0,0,0.3))'
              }}
            >
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2L13.09 8.26L22 9L13.09 9.74L12 16L10.91 9.74L2 9L10.91 8.26L12 2Z"/>
              </svg>
            </div>
          </div>

          {/* Shadow */}
          <div 
            className="absolute bg-dark-500 rounded-lg opacity-30"
            style={{ 
              width: `${barWidth}px`, 
              height: `${barHeight}px`,
              transform: 'rotateX(15deg) translateY(10px) translateX(10px)',
              filter: 'blur(4px)'
            }}
          />
        </div>

        {/* Skill Info Below Bar */}
        <div className="mt-4 text-center">
          <div className="text-2xl mb-2">{skill.icon}</div>
          <div className="font-semibold text-text-primary text-sm mb-1">{skill.name}</div>
          <div className="text-lg font-bold text-accent-500">{skill.level}%</div>
          <div className="text-xs text-text-secondary">{skill.repos} repos</div>
        </div>
      </div>
    );
  };

  return (
    <section id="skills" className="section-padding bg-white">
      <div className="container-max">
        <div className="text-center mb-16">
          <h2 className="text-3xl sm:text-4xl font-bold text-secondary-900 mb-4">
            Skills & Expertise
          </h2>
          <p className="text-lg text-secondary-600 max-w-2xl mx-auto">
            Dynamically fetched from my GitHub repositories and visualized with 3D charts
          </p>
        </div>

        {/* GitHub Stats Overview */}
        <div className="bg-gradient-to-r from-dark-800 to-dark-700 rounded-2xl p-8 mb-16 border border-dark-600">
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-accent-500/20 rounded-full mb-4">
              <TrendingUp className="w-8 h-8 text-accent-500" />
            </div>
            <h3 className="text-2xl font-semibold text-text-primary mb-2">
              GitHub Profile Overview
            </h3>
            <p className="text-text-secondary">
              Real metrics from my development journey
            </p>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-accent-500 mb-2">{githubStats.repositories}</div>
              <div className="text-sm text-text-secondary">Repositories</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-accent-500 mb-2">{githubStats.followers}</div>
              <div className="text-sm text-text-secondary">Followers</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-accent-500 mb-2">{githubStats.stars}</div>
              <div className="text-sm text-text-secondary">Stars</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-accent-500 mb-2">🇮🇳</div>
              <div className="text-sm text-text-secondary">Delhi, India</div>
            </div>
          </div>

          <div className="text-center mt-6">
            <p className="text-accent-400 font-medium">
              🎯 {githubStats.focus}
            </p>
          </div>
        </div>

        {/* 3D Skills Visualization */}
        {loading ? (
          <div className="text-center py-12">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-accent-500/20 rounded-full mb-4">
              <Github className="w-8 h-8 text-accent-500 animate-spin" />
            </div>
            <p className="text-text-secondary">Fetching skills from GitHub...</p>
          </div>
        ) : (
          <div className="mb-16">
            <div className="text-center mb-12">
              <h3 className="text-2xl font-semibold text-text-primary mb-4">
                3D Skills Visualization
              </h3>
              <p className="text-text-secondary">
                Interactive 3D bar charts showing my expertise levels in each technology
              </p>
            </div>

            {/* 3D Bar Charts Grid */}
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-8 justify-items-center">
              {skills.slice(0, 15).map((skill, index) => (
                <div 
                  key={index} 
                  className="transition-all duration-500 transform hover:scale-110"
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  <SkillBarChart skill={skill} />
                </div>
              ))}
            </div>

            {/* Skills Summary */}
            <div className="mt-12 text-center">
              <div className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-accent-500 to-primary-500 text-dark-900 rounded-lg">
                <Star className="w-5 h-5 mr-2" />
                <span className="font-medium">
                  {skills.length} Skills • {skills.reduce((sum, skill) => sum + skill.repos, 0)} Total Repositories
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Top Repositories */}
        {repositories.length > 0 && (
          <div className="bg-gradient-to-r from-dark-800 to-dark-700 rounded-2xl p-8 mb-16 border border-dark-600">
            <div className="text-center mb-8">
              <div className="inline-flex items-center justify-center w-16 h-16 bg-accent-500/20 rounded-full mb-4">
                <Star className="w-8 h-8 text-accent-500" />
              </div>
              <h3 className="text-2xl font-semibold text-text-primary mb-2">
                Top Repositories
              </h3>
              <p className="text-text-secondary">
                My most active and popular projects
              </p>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              {repositories.slice(0, 6).map((repo, index) => (
                <div
                  key={index}
                  className="bg-dark-600/50 backdrop-blur-sm rounded-lg p-6 border border-dark-500 hover:bg-dark-600 hover:border-accent-500/50 transition-all duration-300"
                >
                  <div className="flex items-start justify-between mb-3">
                    <h4 className="font-semibold text-text-primary text-lg">
                      {repo.name}
                    </h4>
                    <a
                      href={repo.html_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-accent-500 hover:text-accent-400 transition-colors duration-200"
                    >
                      <ExternalLink className="w-4 h-4" />
                    </a>
                  </div>

                  {repo.description && (
                    <p className="text-text-secondary text-sm mb-3 line-clamp-2">
                      {repo.description}
                    </p>
                  )}

                  <div className="flex items-center justify-between text-sm text-text-muted">
                    <div className="flex items-center space-x-4">
                      {repo.language && (
                        <span className="flex items-center space-x-1">
                          <span className="w-3 h-3 bg-accent-500 rounded-full"></span>
                          <span>{repo.language}</span>
                        </span>
                      )}
                      <span>⭐ {repo.stargazers_count}</span>
                      <span>🍴 {repo.forks_count}</span>
                    </div>
                    <span>{formatDate(repo.updated_at)}</span>
                  </div>
                </div>
              ))}
            </div>

            <div className="text-center mt-6">
              <a
                href="https://github.com/Nitish-11k?tab=repositories"
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center px-6 py-3 bg-accent-500 text-dark-900 font-semibold rounded-lg hover:bg-accent-600 transition-all duration-300"
              >
                <Github className="w-5 h-5 mr-2" />
                View All Repositories
              </a>
            </div>
          </div>
        )}

        {/* Learning Journey */}
        <div className="text-center mt-16">
          <h3 className="text-2xl font-semibold text-text-primary mb-4">
            Always Learning & Growing
          </h3>
          <p className="text-lg text-text-secondary max-w-3xl mx-auto mb-6">
            Currently diving into <strong className="text-accent-500">Frontend Development</strong> with React and TypeScript, while continuing to 
            expand my knowledge in cloud architecture, microservices, and advanced Java patterns. Working towards 
            becoming a full-stack developer!
          </p>
          <div className="inline-flex items-center px-6 py-3 bg-accent-500/20 text-accent-400 rounded-lg border border-accent-500/30">
            <Star className="w-5 h-5 mr-2" />
            <span className="font-medium">Focusing on Backend Development using Java</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
